// n8n Code node (JavaScript)
const items = $input.all();

return items.map(item => {
  const topic = item.json.Topic || "";
  const keywords = item.json["Keywords or Hashtags (optional)"] || "";
  const link = item.json["Link (Optional)"] || "";

  // Merge into a single prompt
  const prompt = `Topic: ${topic}\nKeywords/Hashtags: ${keywords}\nLink: ${link}`;

  return {
    json: {
      topic,
      keywords,
      link,
      prompt
    }
  };
});

// Input: items array coming from previous node
// Example: [{ json: { data: "{...stringified JSON...}" } }]

const results = [];

for (const item of items) {
  try {
    // Parse the stringified JSON inside data
    const parsed = JSON.parse(item.json.data);

    // Push the parsed JSON as a clean object
    results.push({
      json: parsed
    });

  } catch (error) {
    // If parsing fails, return error
    results.push({
      json: {
        error: "Invalid JSON format",
        details: error.message,
        original: item.json.data
      }
    });
  }
}

return results;

